mapper.insert:
INSERT INTO store_product_qna_tb
(product_id, user_id, question_title, question_content, is_secret, create_date)
VALUES (#{productId}, #{userId}, #{questionTitle}, #{questionContent}, #{secret}, now());

mapper.select:
SELECT qna.*, ut.user_username, ut.user_nickname, p.product_name FROM store_product_qna_tb qna LEFT JOIN user_tb ut ON ut.user_id = qna.user_id LEFT JOIN store_product_tb p ON p.product_id = qna.product_id
WHERE qna.product_id = #{productId}
ORDER BY qna.create_date DESC LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM store_product_qna_tb

    WHERE product_id = #{productId}
