mapper.select:
SELECT
    EXISTS(
SELECT 1
    FROM store_order_tb
WHERE
    user_id = #{userId}
    AND product_id = #{productId}
    AND order_status = 'DELIVERED'
);

SELECT sct.comment_id, sct.product_id, sct.user_id, ut.user_username, ut.user_profile_image_url, sct.comment_text, sct.create_date, sct.update_date, IFNULL(AVG(srrt.rating), 0) AS average_rating, COUNT(srrt.rating) AS rating_count FROM store_comment_tb sct LEFT JOIN store_review_rating_tb srrt ON srrt.comment_id = sct.comment_id LEFT JOIN user_tb ut ON ut.user_id = sct.user_id
WHERE sct.product_id = #{productId}
GROUP BY sct.comment_id ORDER BY sct.create_date DESC LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM store_comment_tb

    WHERE product_id = #{productId}
