mapper.select:
SELECT COUNT(*)
FROM store_review_report_tb
WHERE reporter_id = #{reporterId} AND comment_id = #{commentId};

SELECT r.report_id, r.comment_id, r.reporter_id, r.reason, r.create_date,
u.user_username AS reporter_username, c.comment_text, p.product_name
FROM store_review_report_tb r LEFT JOIN user_tb u ON r.reporter_id = u.user_id LEFT JOIN store_comment_tb c ON r.comment_id = c.comment_id LEFT JOIN store_product_tb p ON c.product_id = p.product_id ORDER BY r.create_date DESC LIMIT #{offset}, #{size}

mapper.update:
UPDATE store_review_report_tb
SET resolved = 1
WHERE report_id = #{reportId}
